
public class JavaExpressions {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double friends = 30;
		friends = friends + 3;
		friends = friends - 1;
		friends = friends * 1;
		friends = friends / 1;
		friends = friends % 1;
		
//		or 
		friends++ ;
		friends-- ;
		
//		cast a value
		friends = (double) friends / 3;
		
		System.out.println(friends);

	}

}
